<script>(function(){const chips = Array.from(document.querySelectorAll('.pub-chip'));const kicker = document.getElementById('focus-kicker');const title = document.getElementById('focus-title');const meta = document.getElementById('focus-meta');const link = document.getElementById('focus-link');if(!chips.length || !kicker || !title || !meta || !link) return;function setActive(el){  chips.forEach(c => c.classList.remove('active'));  el.classList.add('active');  kicker.textContent = el.dataset.theme || '';  title.textContent = el.dataset.title || '';  const j = el.dataset.journal || '';  const y = el.dataset.year || '';  const c = el.dataset.cites || '0';  meta.innerHTML = '<strong>' + j + '</strong><br>' + y + ' · ' + c + ' citations';  link.href = el.href;}chips.forEach((el)=>{  el.addEventListener('mouseenter', ()=>setActive(el));  el.addEventListener('focusin', ()=>setActive(el));});setActive(chips[0]);})();</script>
