/* --- Playful circular publication badges --- */
/* --- Playful circular publication badges --- */
.pub-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 20px;
  margin: 2rem 0 3rem 0;
}
.pub-bubble {
  position: relative;
  border-radius: 50%;
  aspect-ratio: 1 / 1;
  min-height: 180px;
  cursor: pointer;
  overflow: visible;
  transform-origin: center;
  transition: transform 220ms ease;
  outline: none;
}

.pub-bubble::before,
.pub-bubble::after {
  content: "";
  position: absolute;
  inset: -5px;
  border-radius: 50%;
  pointer-events: none;
  border: 1px solid rgba(126, 199, 255, 0.28);
  animation: bubble-pulse 3.2s ease-out infinite;
}

.pub-bubble::after {
  inset: -11px;
  opacity: 0.42;
  animation-delay: 1.4s;
}

.pub-bubble:hover {
  transform: translateY(-2px) scale(1.03);
}

.pub-bubble:focus-visible {
  box-shadow: 0 0 0 3px rgba(126, 199, 255, 0.45);
}

.pub-flip {
  position: absolute;
  inset: 0;
  border-radius: 50%;
  transform-style: preserve-3d;
  transition: transform 1000ms cubic-bezier(0.22, 0.61, 0.36, 1);
}

.pub-bubble:hover .pub-flip,
.pub-bubble:focus-within .pub-flip,
.pub-bubble.is-flipped .pub-flip {
  transform: rotateY(180deg);
}

.pub-face {
  position: absolute;
  inset: 0;
  border-radius: 50%;
  border: 1px solid rgba(255,255,255,0.24);
  background: radial-gradient(circle at 30% 22%, rgba(255,255,255,0.12), rgba(255,255,255,0.05) 45%, rgba(255,255,255,0.02) 100%);
  box-shadow: 0 8px 24px rgba(0,0,0,0.35);
  color: #d9d9d9;
  padding: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  backface-visibility: hidden;
}

.pub-front {
  z-index: 2;
}

.pub-back {
  transform: rotateY(180deg);
  flex-direction: column;
  gap: 0.45rem;
}

.pub-title {
  font-weight: 600;
  line-height: 1.25;
  font-size: 0.85rem;
}

.pub-meta {
  font-size: 0.8rem;
  line-height: 1.35;
  color: #e6eefc;
}

.pub-open {
  color: #f4f8ff !important;
  text-decoration: none;
  border: 1px solid rgba(255,255,255,0.34);
  background: rgba(255,255,255,0.08);
  border-radius: 999px;
  padding: 0.24rem 0.62rem;
  font-size: 0.78rem;
  font-weight: 600;
}

.pub-open:hover {
  background: rgba(255,255,255,0.17);
}

.pub-grid { perspective: 1200px; }
.pub-grid { overflow: visible; }
.pub-bubble { will-change: transform; }

@keyframes bubble-pulse {
  0% {
    transform: scale(1);
    opacity: 0.55;
  }
  75% {
    transform: scale(1.12);
    opacity: 0;
  }
  100% {
    transform: scale(1.12);
    opacity: 0;
  }
}

/* Respect reduced motion */
@media (prefers-reduced-motion: reduce) {
  .pub-bubble,
  .pub-flip {
    transition: none;
  }
  .pub-bubble::before,
  .pub-bubble::after {
    animation: none;
  }
  .pub-bubble .pub-flip {
    transform: none !important;
  }
}



