( function(){
  const grid = document.querySelector('.pub-grid');
  if(!grid) return;

  function closeOthers(current){
    grid.querySelectorAll('.pub-bubble.is-flipped').forEach((el)=>{
      if(el !== current) el.classList.remove('is-flipped');
    });
  }

  grid.addEventListener('click', (e)=>{
    const link = e.target.closest('.pub-open');
    if(link) return;
    const bubble = e.target.closest('.pub-bubble');
    if(!bubble) return;
    e.preventDefault();
    const willFlip = !bubble.classList.contains('is-flipped');
    closeOthers(bubble);
    bubble.classList.toggle('is-flipped', willFlip);
  });

  grid.addEventListener('keydown', (e)=>{
    const bubble = e.target.closest('.pub-bubble');
    if(!bubble) return;
    if(e.key === 'Enter' || e.key === ' '){
      e.preventDefault();
      const willFlip = !bubble.classList.contains('is-flipped');
      closeOthers(bubble);
      bubble.classList.toggle('is-flipped', willFlip);
    }
    if(e.key === 'Escape'){
      bubble.classList.remove('is-flipped');
    }
  });

  document.addEventListener('click', (e)=>{
    if(e.target.closest('.pub-bubble')) return;
    grid.querySelectorAll('.pub-bubble.is-flipped').forEach((el)=>el.classList.remove('is-flipped'));
  });
})();
</script>
