</div><div class="pub-bubble" data-venue="JAMA pediatrics" data-cites="14" data-link="https://scholar.google.com/citations?view_op=view_citation&amp;user=nYA8PAsAAAAJ&amp;citation_for_view=nYA8PAsAAAAJ:IWHjjKOFINEC" role="button" tabindex="0">
  <div class="pub-title">Improving antibiotic stewardship for diarrheal disease with probability-based electronic clinical decision support: a randomized crossover trial (2022)</div>
</div></div><script>
( function(){
  const grid = document.querySelector('.pub-grid');
  if(!grid) return;
  let tip = document.createElement('div');
  tip.className = 'pub-tooltip';
  document.body.appendChild(tip);
  let currentEl = null;

  function showTip(el){
    currentEl = el;
    const venue = el.getAttribute('data-venue') || '';
    const cites = el.getAttribute('data-cites') || '';
    const link  = el.getAttribute('data-link')  || '#';
    tip.innerHTML = `<div><strong>Journal:</strong> ${venue}</div>`+
                    `<div><strong>Citations:</strong> ${cites}</div>`+
                    `<div class="tip-actions"><a href="${link}" target="_blank" rel="noopener">Scholar</a></div>`;
    positionTip(el);
    tip.classList.add('show');
  }

  function positionTip(el){
    const r = el.getBoundingClientRect();
    const x = r.left + r.width/2; const y = r.top;
    tip.style.left = Math.max(12, Math.min(window.innerWidth-12-320, x-160)) + 'px';
    tip.style.top  = (window.scrollY + y - tip.offsetHeight - 12) + 'px';
  }

  grid.addEventListener('mouseover', e=>{
    const el = e.target.closest('.pub-bubble');
    if(!el) return; showTip(el);
  });
  grid.addEventListener('mousemove', e=>{
    if(!tip.classList.contains('show')) return; 
    const el = e.target.closest('.pub-bubble') || currentEl;
    if(!el) return; positionTip(el);
  });
  grid.addEventListener('mouseout', e=>{
    const to = e.relatedTarget;
    if(to && (to === tip || (to.closest && to.closest('.pub-tooltip')))) return; // stay visible when entering tooltip
    const leavingBubble = e.target.closest && e.target.closest('.pub-bubble');
    const toBubble = to && to.closest && to.closest('.pub-bubble');
    if(leavingBubble && toBubble === leavingBubble) return; // moving inside same bubble
    if(!toBubble) { currentEl = null; hideTip(); }
  });
  // Open Scholar on click/Enter
  grid.addEventListener('click', e=>{
    const el = e.target.closest('.pub-bubble');
    if(!el) return; const link = el.getAttribute('data-link');
    if(link) window.open(link, '_blank', 'noopener');
  });
  grid.addEventListener('keydown', e=>{
    if(e.key === 'Enter' || e.key === ' ') {
      const el = e.target.closest('.pub-bubble');
      if(!el) return; const link = el.getAttribute('data-link');
      if(link) window.open(link, '_blank', 'noopener');
    }
  });
  tip.addEventListener('mouseleave', ()=>{ currentEl = null; hideTip(); });
  tip.addEventListener('mouseenter', ()=>{ /* keep visible */ });

  // Ensure clicks on links inside the tooltip always work
  tip.addEventListener('click', (e)=>{
    const a = e.target.closest('a[href]');
    if(!a) return;
    e.preventDefault();
    try { window.open(a.href, '_blank', 'noopener'); }
    catch(_) { location.href = a.href; }
  });

  function hideTip(){ tip.classList.remove('show'); }

})();
</script>
